const rows = $input.all().map(i => i.json);     // якщо SQL повертає N рядків
const week = {
  start: rows[0]?.first_day || null,
  end:   rows[0]?.last_day  || null,
  limit: $vars.PM25_LIMIT ?? 10
};

return [{
  json: {
    week,
    rows: rows.map(r => ({
      oblast: r.oblast,
      mean:   Number(r.mean),
      peak:   Number(r.peak),
      exceed_hours: Number(r.exceed_hours),
      max_stations: Number(r.max_stations)
    }))
  }
}];

const src0 = $input.first()?.json ?? {};
const src = (src0 && src0.output) ? src0.output : src0;

const errors = [];
const warnings = [];

const asString = (v) => (v == null ? '' : String(v)).trim();

const asNumber = (v) => {
  if (typeof v === 'number') return v;
  if (typeof v === 'string' && v.trim() !== '' && !Number.isNaN(Number(v))) {
    return Number(v);
  }
  return NaN;
};

const pushErr = (path, msg) => errors.push(`${path}: ${msg}`);
const pushWarn = (path, msg) => warnings.push(`${path}: ${msg}`);

const isISO = (s) => {
  try {
    if (typeof s !== 'string') return false;
    const d = new Date(s);
    if (Number.isNaN(d.getTime())) return false;
    return /T/.test(s);
  } catch (_) {
    return false;
  }
};

const clamp = (x, lo, hi) =>
  Number.isFinite(x) ? Math.min(Math.max(x, lo), hi) : x;

const uniq = (arr) => Array.from(new Set(arr));

const checkNonNeg = (n, path) => {
  if (!Number.isFinite(n)) {
    pushErr(path, 'must be a finite number');
    return null;
  }
  if (n < 0) {
    pushWarn(path, `negative (${n}) → clamped to 0`);
    return 0;
  }
  return n;
};

const checkInRange = (n, lo, hi, path) => {
  if (!Number.isFinite(n)) {
    pushErr(path, 'must be a finite number');
    return null;
  }
  if (n < lo || n > hi) {
    pushWarn(path, `out of range [${lo}; ${hi}] (${n}) → clamped`);
    return clamp(n, lo, hi);
  }
  return n;
};

const out = {
  week: { start: '', end: '' },
  highlights: [],
  summary: '',
};

const week = src.week ?? {};
const start = asString(week.start);
const end = asString(week.end);

if (!start) pushErr('week.start', 'is required');
if (!end) pushErr('week.end', 'is required');

if (start && !isISO(start)) pushErr('week.start', 'is not ISO-like date');
if (end && !isISO(end)) pushErr('week.end', 'is not ISO-like date');

if (isISO(start) && isISO(end)) {
  const d1 = new Date(start).getTime();
  const d2 = new Date(end).getTime();
  if (d2 < d1) pushErr('week', 'end < start');
  const days = (d2 - d1) / (1000 * 3600 * 24);
  if (Number.isFinite(days) && days > 14) {
    pushWarn('week', `period is ${days.toFixed(1)} days (> 14)`);
  }
}

out.week.start = start;
out.week.end = end;

let highlights = src.highlights;
if (!Array.isArray(highlights)) {
  highlights = [];
  pushWarn('highlights', 'not an array → empty[]');
}

highlights.forEach((h, i) => {
  const path = `highlights[${i}]`;

  const oblast = asString(h?.oblast);
  if (!oblast) pushErr(`${path}.oblast`, 'is required and must be a non-empty string');

  let mean = asNumber(h?.mean);
  mean = checkInRange(mean, 0, 500, `${path}.mean`); 

  let peak = asNumber(h?.peak);
  peak = checkInRange(peak, 0, 1000, `${path}.peak`); 

  let exceed_hours = asNumber(h?.exceed_hours);
  exceed_hours = checkInRange(exceed_hours, 0, 24 * 7, `${path}.exceed_hours`);

  let max_stations = asNumber(h?.max_stations);
  max_stations = checkInRange(max_stations, 0, 1000, `${path}.max_stations`);

  let drivers = Array.isArray(h?.drivers) ? h.drivers.map(asString).filter(Boolean) : [];
  if (!Array.isArray(h?.drivers)) {
    pushWarn(`${path}.drivers`, 'not an array → []');
  }
  drivers = uniq(drivers).slice(0, 10); 

  out.highlights.push({
    oblast,
    mean,
    peak,
    exceed_hours,
    max_stations,
    drivers,
  });
});

const summary = asString(src.summary);
if (summary.length > 1000) {
  pushWarn('summary', `too long (${summary.length}) → trimmed to 1000 chars`);
}
out.summary = summary.slice(0, 1000);

const ok = errors.length === 0;

const issuesText =
  (errors.length ? `Errors:\n- ${errors.join('\n- ')}\n` : '') +
  (warnings.length ? `Warnings:\n- ${warnings.join('\n- ')}` : '');

return [
  {
    json: {
      ok,
      errors,
      warnings,
      data: out,
      issues_text: issuesText || 'No issues',
    },
  },
];
